import React from 'react';
import './App.css';
import Home from './Home.js';
import Login from './Login.js';
import Signup from './Signup.js';
import { BrowserRouter, Switch, Route, Redirect } from "react-router-dom";

function App() {
  return (
    <BrowserRouter>
      <Switch>
        <Route path="/login">
          <Login />
        </Route>
        <Route path="/signup">
          <Signup />
        </Route>
        <Route path="/home">
          <Home />
        </Route>
        <Route exact path="/">
          <Redirect exact from="/" to="home" />
        </Route>
        <Route path="*">
          <Redirect exact from="/" to="home" />
        </Route>
      </Switch>
    </BrowserRouter>
  );


  const isLoggedIn = props.isLoggedIn;
  if(isLoggedIn) {
    return (
      <div className="App">
        <div className="App-header">
          <strong>ReadingAid</strong>
        </div>
        <Home />
      </div>
    );
  } else {
    return (
      <div className="App">
        <div className="App-header">
          <strong>ReadingAid</strong>
        </div>
        <Login />
      </div>
    );
  }
}

export default App;
