import React, { Component } from "react";
import firebase from "firebase/app";
import "firebase/database";
import "./Login.css";
import { Link, Route } from "react-router-dom";
import Home from "./Home.js";

class Login extends Component {
    constructor(props) {
        super(props);
        this.props = {
            hasAccount: true,
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange(event) {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({[name]: value});
    }

    handleSubmit(event) {
        var db = firebase.database();
        // check if a user with this name exists
        db.ref('users').child(this.props.username).get().then((snapshot) => {
            if(snapshot.exists()) {
                // if so, check if the password is correct
                if(snapshot.val().password === this.props.password) {
                    this.setState({redirect: true});
                } else {
                    alert("Password is incorrect, please try again.");
                }
            } else {
                alert("There is no user '" + this.props.username + "'. Please try again or create a new account.");
            }
        }).catch((error) => {
            console.error(error);
        });

        event.preventDefault();
    }

    render() {
        return (
            <form id="login-form" className="text-center">
                <legend><h3>Sign In</h3></legend>
                <div className="form-group">
                    <input name="username" type="username" className="form-control form-control-lg" placeholder="Username" value={this.props.username} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <input name="password" type="password" className="form-control form-control-lg" placeholder="Password" value={this.props.password} onChange={this.handleInputChange} />
                </div>
                <a id="create-account-link" className="form-text text-muted" onClick={this.props.onClick}>Don't have an account? Create one here.</a>
                <div className="form-group">
                    <input id="login-submit" type="submit" className="btn btn-lg btn-secondary" value="SUBMIT" onClick={this.props.submitOnClick} />
                </div>
            </form>
        );
    }
}

export default Login;
