import { Component } from "react";
import "./Home.css";
import firebase from "firebase/app";
import "firebase/database";
import CreateClassroom from "./CreateClassroom";
import ClassOverview from "./ClassOverview";

class Home extends Component {
    constructor(props) {
        super(props);
        this.createClassroom = this.createClassroom.bind(this);
        this.state = {
            userdata: firebase.database().ref('teachers').child(this.props.user),
            isCreatingClassroom: false,
        }
    }

    createClassroom() {
        this.setState({ isCreatingClassroom: true });
    }

   render() {
        let component;
        if(this.state.isCreatingClassroom) {
            component = <CreateClassroom username={this.props.user} />;
        } else {
            component = <ClassOverview user={this.state.userdata} />;
        }

        return isCreatingClassroom ? <CreateClassroom username={this.props.user} /> : <ClassOverview user={this.state.userdata} handleLogout={this.props.handleLogout} />;
   }
}

export default Home;
