import { Component } from "react";
import "./Home.css";

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: firebase.database().ref('teachers').child(this.props.user),
            isCreatingClassroom: false,
        }
    }

   render() {
        let component;
        if(this.state.isCreatingClassroom) {
            component = <CreateClassroom username={this.state.user.name} />;
        } else {
            component = <ClassOverview user={this.state.user} />;
        }

        return (
            <div id="homepage-container" className="container-fluid">
                <h1 className="text-center">Welcome {this.props.user}!</h1>
                <div id="create-classroom-btn">
                    <button className="btn btn-lg btn-secondary">Create Classroom</button>
                    <button className="btn btn-lg btn-secondary" onClick={this.props.handleLogout}>Logout</button>
                </div>
            </div>
        )
   }
}

export default Home;
