import React from "react";
import firebase from "firebase/app";
import "firebase/database";
import { BsArrowLeft } from "react-icons/bs";
import { FaUserCircle } from "react-icons/fa";

class SpecificStudentView extends React.Component {
    constructor(props) {
        super(props);
        this.showTexts = this.showTexts.bind(this);
        this.populateTable = this.populateTable.bind(this);
        this.state = {
            student: null,
            loading: true,
        }
    }

    componentDidMount() {
        firebase.database().ref("students").child(this.props.uid).once("value").then(snapshot => {
            this.setState({ student: snapshot.val(), loading: false });
        });
    }

    showTexts() {
        return Object.values(this.state.student.texts).map(text => {
            return <button key={text.title} className="dropdown-item" onClick={() => {this.populateTable(text.title)}}>{text.title}</button>;
        });
    }

    populateTable(title) {
        console.log("Populating table from book " + title);
    }

    render () {
        const loading = this.state.loading;
        const student = this.state.student;
        return !loading && (
            <div id="student-view-container">
                <div id="back-arrow" type="button" role="button" onClick={this.props.showDashboard}><BsArrowLeft /></div>
                <div id="student-info">
                    <div id="photo-container">
                        <FaUserCircle />
                    </div>
                    <div id="name-container">
                        <h1>{student.username}</h1>
                        <h2>12345</h2>
                    </div>
                </div>
                <button className="btn btn-lg dark-btn mt-3" onClick={this.props.assignTexts}>Assign New Text</button>
                {student.texts && 
                <div id="student-stats">
                    <div className="dropdown">
                        <button className="btn btn-lg light-btn mt-3 dropdown-toggle" type="button" id="texts-dropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                            Books
                        </button> 
                        <div className="dropdown-menu" aria-labelledby="texts-dropdown">
                            {this.showTexts()}
                        </div>
                    </div>
                    <table className="mt-3">
                        <tbody>
                            <tr>
                                <th>Total Time</th>
                                <td></td>
                            </tr>
                            <tr>
                                <th>Number of words read</th>
                                <td></td>
                            </tr>
                            <tr>
                                <th>words per minute</th>
                                <td></td>
                            </tr>
                            <tr>
                                <th>Difficult Words</th>
                                <td></td>
                            </tr>
                        </tbody>  
                    </table>
                </div>}
            </div>
        );
    }
}

export default SpecificStudentView;
