import "./Home.css";
import "firebase/database";
import CreateClassroom from "./CreateClassroom";
import ClassOverview from "./ClassOverview";
import ShowClassroom from "./ShowClassroom";

class Home extends Component {
    constructor(props) {
        super(props);
        this.switchView = this.switchView.bind(this);
        this.state = {
            isCreatingClassroom: false,
            isShowingClassroom: false,
            currClassroom: '',
        }
    }

    switchView() {
        this.setState({ isCreatingClassroom: !this.state.isCreatingClassroom });
    }

    showClassroom(classroom) {
        this.setState({ currClassroom: classroom, isShowingClassroom: true });
    }

