import React, { Component } from "react";
import firebase from "firebase/app";
import "firebase/database";
import "./Login.css";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasAccount: true,
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange(event) {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({[name]: value});
    }

    handleSubmit(event) {
        event.preventDefault();
        // get info from form
		const username = this.state.username;
		const password = this.state.password;
		
		// check that user w/ that username/password exists
		const db = firebase.database();
		db.ref('teachers').child(username).get().then((snapshot) => {
			if(snapshot.exists() && snapshot.val().password === password) {
				// log the user in
                console.log("Name is " + username);
				this.props.handleLogin(true, username);
			} else {
				alert("Invalid username or password, please try again.");
			}
		});
    }

    render() {
        return (
            <form id="login-form" className="text-center">
                <legend><h3>Sign In</h3></legend>
                <div className="form-group">
                    <input name="username" type="username" className="form-control form-control-lg" placeholder="Username" value={this.props.username} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <input name="password" type="password" className="form-control form-control-lg" placeholder="Password" value={this.props.password} onChange={this.handleInputChange} />
                </div>
                <a id="create-account-link" href="#" className="form-text text-muted" onClick={this.props.switchView}>Don't have an account? Create one here.</a>
                <div className="form-group">
                    <input id="login-submit" type="submit" className="btn btn-lg btn-secondary" value="SUBMIT" onClick={this.props.handleSubmit} />
                </div>
            </form>
        );
    }
}

export default Login;
