import React, { Component } from 'react';
import './login.css'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            password: '',
        };
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ 
            [name]: value
        });
        console.log("Changing " + name + " to " + value);
    }

    render() {
        return (
            <form id="signup-form">
                <h4 className="text-center">Create an Account</h4>
                <div className="form-group">
                    <label htmlFor="first-name">Name: </label>
                    <input name="first-name" type="text" className="form-control" value={this.state.first_name} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="last-name">Name: </label>
                    <input name="last-name" type="text" className="form-control" value={this.state.last_name} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email: </label>
                    <input name="email" type="email" className="form-control" value={this.state.email} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="username">Confirm password: </label>
                    <input name="username" type="username" className="form-control" value={this.state.username} onChange={this.handleInputChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password: </label>
                    <input name="password" type="password" className="form-control" value={this.state.password} onChange={this.handleInputChange} />
                </div>
                <div className="form-group text-center">
                    <input id="signup-submit" type="submit" className="btn btn-secondary" value="CREATE" />
                </div>
            </form>
        );
    }
}
    
export default Login;
