import React from 'react';
import './login.css'

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            password: '',
        };
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ [name]: value });
        console.log("Changing " + name + " to " + value);
    }

    render() {
        return (
            <div className="Login">
                <header className="login-header">
                    <h2>Welcome to Reading Aid</h2>
                </header>
                <form id="signup-form">
                    <div className="form-group">
                        <label for="name">Name: </label>
                        <input id="name" type="text" className="form-control" value={this.state.name} onChange={this.handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label for="email">Email: </label>
                        <input id="email" type="text" className="form-control" value={this.state.email} onChange={this.handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label for="password">Password: </label>
                        <input id="password" type="text" className="form-control" value={this.state.password} onChange={this.handleInputChange} />
                    </div>
                    {/* <div className="form-group">
                        <label for="confirm-password">Confirm password: </label>
                        <input id="confirm-password" type="text" className="form-control" value={this.state.confirm_password} onChange={this.handleInputChange} />
                    </div> */}
                    <button id="signup-submit" type="submit" className="btn btn-secondary">Create Account</button>
                </form>
            </div>
        );
    }
}
    
export default Login;
