import React from "react";
import firebase from "firebase/app";
import "firebase/database";
import { BsArrowLeft } from "react-icons/bs";
import { FaUserCircle } from "react-icons/fa";

class SpecificStudentView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            student: null,
            loading: true,
        }
    }

    componentDidMount() {
        firebase.database().ref("students").child(this.props.uid).once("value").then(snapshot => {
            this.setState({ student: snapshot.val(), loading: false });
        });
    }

    render () {
        const loading = this.state.loading;
        const student = this.state.student;
        console.log(student);
        return !loading && (
            <div id="student-view-container">
                <div id="back-arrow" type="button" role="button" onClick={this.props.showDashboard}><BsArrowLeft /></div>
                <div id="student-info">
                    <div id="photo-container">
                        <FaUserCircle />
                    </div>
                    <div id="name-container">
                        <h1>{student.username}</h1>
                        <h2>12345</h2>
                    </div>
                </div>
                <button className="btn btn-lg dark-btn mt-3">Assign New Text</button>
            </div>
        );
    }
}

export default SpecificStudentView;
