import React, { Component } from 'react';
import './login.css'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            password: '',
        };
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ 
            [name]: value
        });
        console.log("Changing " + name + " to " + value);
    }

    render() {
        return (
            <form id="signup-form" className="text-center">
                <h4>Create an Account</h4>
                <input name="first-name" type="text" className="form-control" placeholder="first name" value={this.state.first_name} onChange={this.handleInputChange} />
                <input name="last-name" type="text" className="form-control" placeholder="last name" value={this.state.last_name} onChange={this.handleInputChange} />
                <input name="email" type="email" className="form-control" placeholder="email" value={this.state.email} onChange={this.handleInputChange} />
                <input name="username" type="username" className="form-control" placeholder="username" value={this.state.username} onChange={this.handleInputChange} />
                <input name="password" type="password" className="form-control" placeholder="password" value={this.state.password} onChange={this.handleInputChange} />
                <input id="signup-submit" type="submit" className="btn btn-secondary" value="CREATE" />
            </form>
        );
    }
}
    
export default Login;
